	if(delivery_road is null,'비어있음',delivery_road),
	if(delivery_detail is null,'비어있음',delivery_detail),
	if(address_code_group_id is null,'비어있음',address_code_group_id),
	if(address_code_id is null,'비어있음',address_code_id),
	member_state_id
from tbl_member_detail
where user_id = #{user_id}
